import { createSlice,fetchBaseQuery } from "@reduxjs/toolkit";
import { BASE_URL } from "../../utils/constants";


export const apiSclice = createAp({
reducerPath:'api',
basQuery:fetchBaseQuery({baseUrl:BASE_URL}),
tagTypes:['Product'],
andPoints:(builder) => ({
  getQuery:builder.query({
    query:({id}) => `/products/${id}`,
    providesTags:['products'],
  })
})
})

export const {useGetProductQuery} = apiSclice;
